<script lang="ts">
export default {
  name: 'ThemeSetting'
}
</script>
<script lang="ts" setup>
import { ref, watch } from 'vue'
import { LayoutStore } from './LayoutStore'
import { LayoutMode } from './GlLayout'

const emits = defineEmits(['update:modelValue'])
const props = defineProps({
  modelValue: {
    type: String,
    default() {
      return ''
    }
  }
})
const mv = ref(props.modelValue)
watch(mv, () => {
  emits('update:modelValue', mv.value)
})
const layoutStore = LayoutStore()

const colors = [{ color: '#6366F1' }, { color: '#0EA5E9' }, { color: '#A855F7' }, { color: '#8B5CF6' }]
</script>

<template>
  <div class="gl-theme-setting">
    <div class="gl-left">
      <div class="gl-title">选择布局</div>
      <div class="gl-items">
        <div @click="layoutStore.changeLayoutMode(LayoutMode.Sidebar)">
          <img
            src="data:image/svg+xml;base64,"
          />
        </div>
        <div @click="layoutStore.changeLayoutMode(LayoutMode.Collapse)">
          <img
            src="data:image/svg+xml;base64,"
          />
        </div>
        <div @click="layoutStore.changeLayoutMode(LayoutMode.TopNav)">
          <img
            src="data:image/svg+xml;base64,"
          />
        </div>
      </div>
    </div>
    <div class="gl-right">
      <div class="gl-title">选择颜色</div>
      <div>
        <div class="gl-color" :style="{ 'background-color': item.color }" v-for="item in colors">
        </div>
      </div>
    </div>
  </div>
</template>

